import { useState, useEffect } from "react";
import { linkData } from "../../lib/data";

export default function Header() {
  const [activeSection, setActiveSection] = useState("Home");

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll("section");
      const scrollPosition = window.scrollY + window.innerHeight / 2;

      sections.forEach((section) => {
        if (
          scrollPosition >= section.offsetTop &&
          scrollPosition < section.offsetTop + section.offsetHeight
        ) {
          setActiveSection(section.id);
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <header className="z-[999] relative w-full">
      <div className="fixed w-full flex flex-col justify-center items-center bg-bkg-2/60 border border-text/10 h-[7.25rem] inset-0 my-0 mx-auto shadow-lg shadow-black/[0.1] md:w-[60rem] md:h-[5.5rem] md:rounded-full md:top-5 2xl:w-[80rem]">
        <nav className="h-12 flex fixed left-1/2 -translate-x-1/2 sm:h-[initial] sm:rounded-full">
          {/* logo img */}
          <div className="relative">
            <img
              src="/catLogo.png"
              alt="logo"
              className="h-16 w-16 m-5 float-left sm:h-20 sm:w-20 transition-transform duration-300 ease-in-out hover:translate-y-20 transition-opacity duration-600 hover:opacity-0"
            />
          </div>
          <ul className="flex flex-wrap gap-5 justify-center text-base items-center w-[28rem] h-[inherit] sm:w-[40rem]">
            {linkData.map((link) => (
              <li
                key={link.id}
                className="relative flex justify-center items-center h-3/4 px-5"
              >
                <a
                  href={link.url}
                  data-name={link.name}
                  onClick={() => setActiveSection(link.name)}
                  className={`flex justify-center items-center transition-colors duration-500 ${
                    activeSection === link.name
                      ? "text-[#9f0287] font-bold"
                      : "text-white hover:text-[#9f0287]"
                  }`}
                >
                  {link.name}
                  {activeSection === link.name && "();"}
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </header>
  );
}
