import { useState, useEffect } from "react";
import { linkData } from "../../lib/data";

export default function Header() {
  const [activeSection, setActiveSection] = useState("Home");

  useEffect(() => {
    document.querySelector('[data-name="Home"]').click();
    document.querySelector('[data-name="Home"]').innerHTML = "Home();";
    document.querySelector('[data-name="Home"]').style.transition =
      "color 0.5s";
    document.querySelector('[data-name="Home"]').style.color = "#9f0287";

    window.onscroll = function () {
      let links = document.querySelectorAll("a");
      links.forEach((link) => {
        if (link.getAttribute("data-name") === activeSection) {
          link.innerHTML = link.getAttribute("data-name") + "();";
          link.style.transition = "color 0.5s";
          link.style.color = "#9f0287";
        } else {
          link.style.color = "#fff";
        }
      });
    };
  }, [activeSection]);

  function linkSelected() {
    let links = document.querySelectorAll("a");
    links.forEach((link) => {
      if (link.getAttribute("data-name") === activeSection) {
        link.style.transition = "color 0.5s";
        link.style.color = "#9f0287";
      } else {
        link.style.color = "#fff";
      }
    });
  }

  return (
    <header className="transition | fixed w-full flex flex-col justify-center items-center bg-bkg-2/60 border border-text/10 h-[7.25rem] inset-0 my-0 mx-auto shadow-lg shadow-black/[0.1] | md:w-[40rem] md:h-[3.5rem] md:rounded-full md:top-5 | 2xl:w-[50rem]">
      <nav className="h-12 flex fixed top-[.5rem] left-1/2 -translate-x-1/2 | sm:h-[initial] sm:top-6 sm:rounded-full">
        <ul className="transition | flex flex-wrap gap-5 justify-center text-base items-center w-[28rem] h-[inherit] | sm:w-[40rem]">
          {linkData.map((link) => (
            <li
              key={link.id}
              className="relative flex justify-center items-center h-3/4 px-5"
            >
              <a
                data-name={link.name}
                className="flex justify-center items-center"
                onClick={() => {
                  setActiveSection(link.name);
                  linkSelected();
                }}
              >
                {link.name}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}
