import { linkData } from "../../lib/data";

export default function Header() {
  return (
    <header className="z-[999] relative w-full">
      <div className="fixed w-full flex flex-col justify-center items-center bg-bkg-2/60 border border-text/10 h-[7.25rem] inset-0 my-0 mx-auto shadow-lg shadow-black/[0.1] md:w-[40rem] md:h-[3.5rem] md:rounded-full md:top-5 2xl:w-[50rem]"></div>

      <nav className="h-12 flex fixed top-[.5rem] left-1/2 -translate-x-1/2 sm:h-[initial] sm:top-6 sm:rounded-full">
        <ul className="flex flex-wrap gap-5 justify-center text-base items-center w-[28rem] h-[inherit] sm:w-[40rem]">
          {linkData.map((link) => (
            <li
              key={link.id}
              className="relative flex justify-center items-center h-3/4 px-5"
            >
              <a
                href={link.url}
                data-name={link.name}
                className={`flex justify-center items-center transition-colors duration-500 ${
                  link.name === "Home"
                    ? "text-[#9f0287] font-bold"
                    : "text-white hover:text-[#9f0287]"
                }`}
              >
                {link.name}
                {link.name === "Home" && "();"}
              </a>
              {/* You can add similar conditions for other links */}
              {linkData.map((link) => (
                <li
                  key={link.id}
                  className="relative flex justify-center items-center h-3/4 px-5"
                >
                  <a
                    href={link.url}
                    data-name={link.name}
                    className={`flex justify-center items-center transition-colors duration-500 ${
                      activeSection === link.name
                        ? "text-[#9f0287] font-bold"
                        : "text-white hover:text-[#9f0287]"
                    }`}
                  >
                    {link.name}
                    {activeSection === link.name && "();"}
                  </a>
                </li>
              ))}
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}
