import { useState, useRef } from "react";
import { linkData } from "../../lib/data";
import { motion } from "framer-motion";
import { li } from "framer-motion/client";

export default function Header() {
  const [activeSection, setActiveSection] = useState("Home");
  return (
    <header className="z-[999] relative w-full">
      <div className="transition | fixed w-full flex flex-col justify-center items-center bg-bkg-2/60 border border-text/10 h-[7.25rem] inset-0 my-0 mx-auto shadow-lg shadow-black/[0.1] | md:w-[40rem] md:h-[3.5rem] md:rounded-full md:top-5 | 2xl:w-[50rem]"></div>

      <nav className="h-12 flex fixed top-[.5rem]  left-1/2 -translate-x-1/2 | sm:h-[initail] sm:top-6 sm:rounded-full">
        <ul className="transition | flex flex-wrap gap-5 justify-center text-base items-center w-[28rem] h-[inherit] | sm:w-[40rem] ">
          {linkData.map((link) => (
            <li
              key={link.id}
              className="relative flex justify-center items-center h-3/4 px-5 "
            >
              <a
                href={link.url}
                name={link.name}
                className=" flex justify-center items-center "
                onClick={() => {
                  setActiveSection(link.name);
                  linkSelected();
                }}
              >
                {link.name}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}
onload = function () {
  // home selecded by default
  document.getElementsByName("Home")[0].click();
  //change Home to <Home/>
  document.getElementsByName("Home")[0].innerHTML = "Home();";
  // change color with transition
  // transition
  document.getElementsByName("Home")[0].style.transition = "color 0.5s";
  document.getElementsByName("Home")[0].style.color = "#9f0287";
};

// on scroll change color
window.onscroll = function () {
  // get all links
  let links = document.getElementsByTagName("a");
  // loop through all links
  for (let i = 0; i < links.length; i++) {
    // check if the link is active
    if (links[i].name === activeSection) {
      // change color
      links[i] = links[i].name + "();";
      links[i].style.transition = "color 0.5s";
      links[i].style.color = "#9f0287";
    } else {
      // change color
      links[i].style.color = "#fff";
    }
  }
};

function linkSelected() {
  // get all links
  let links = document.getElementsByTagName("a");
  // loop through all links
  for (let i = 0; i < links.length; i++) {
    // check if the link is active
    if (links[i].name === activeSection) {
      // change color
      links[i].style.transition = "color 0.5s";
      links[i].style.color = "#9f0287";
    } else {
      // change color
      links[i].style.color = "#fff";
    }
  }
}
